import { useState, useEffect } from 'react';
import './links.css';
import { FiArrowDownLeft, FiArrowLeft, FiLink, FiTrash } from 'react-icons/fi';
import { Link } from 'react-router-dom';

import { getLinksSave, deleteLink } from '../../services/storelinks'
import LinkItem from '../../components/LinkItem'

export default function Links() {


    const [myLinks, setMyLinks] = useState([]);
    const [data, setData] = useState({});
    const [showModal, setShowModal] = useState(false);
    const [emptyList, setEmpetyList] = useState(false);

    useEffect(() => {
      async function getLinks(){
        const result = await getLinksSave('@encurtador')

        if(result.length === 0){ // se a lista estiver vazia
          setEmpetyList(true); 
        }

        setMyLinks(result);

      }

      getLinks();

    }, [])

    function handleOpenLink(Link){
      setData(Link)
      setShowModal(true);
    }

    async function handleDelete(id){
      const result = await deleteLink(myLinks, id);

      if(result.length === 0){
        setEmpetyList(true);
      }

      setMyLinks(result)
    }

    return (
      <div className='links-container'>

        <div className='links-header'>
            <Link to="/">
              <FiArrowLeft size={38} color='#FFF'/>
            </Link>
            <h1>Meus Links</h1>
        </div>

        { }

        { myLinks.map( Link => (
                  <div key={Link.id} className='links-item'>
                  <button className='link' onClick={() => handleOpenLink(Link)}>
                    <FiLink size={18} color='#FFF' />
                    {Link.long_url}
                  </button>
                  <button className='link-delete' onClick={ () => handDelete(link.id)}>
                    <FiTrash size={24} color='#FF5454'/>
                  </button> 
              </div>
        )) }

        { showModal && (
          <LinkItem
            closeModal={() => setShowModal(false)}
            content={data}
          />
        )}

      </div>
    )
  }
